package ua.danit.servlet;
import ua.danit.entity.User;

import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.List;

@WebServlet(urlPatterns = "/users")
public class UsersServlet extends HttpServlet{
    List<User> users = new ArrayList<>();
    private static Boolean choice;
    private int counter = 0;
    @Override
    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        PrintWriter writer = resp.getWriter();
        writer.write("<!DOCTYPE html>\n" +
                "<html lang=\"en\">\n" +
                "<head>\n" +
                "    <meta charset=\"UTF-8\">\n" +
                "    <title>Index</title>\n" +
                "</head>\n" +
                "<body>\n" +
                "<h1>Joanna</h1>\n" +
                "<img width=\"400\" src=\"data:image/jpeg;base64,\">\n" +
                "<form acton='/users' method='POST'>\n" +
                "<br><button name=\"choise\" value='Yes'>Yes</button>\n" +
                "<button name=\"choise\" value='No'>No</button>\n" +
                "</form>\n" +
                "</body>\n" +
                "</html>");
    }

    @Override
    public void init() throws ServletException {
        users.add(new User("SluttySlut", "data:image/jpeg;base64,"));
        users.add(new User("Joanna", "image/jpeg;base64,"));
        users.add(new User("Fifa", "https://www.wikihow.com/images/thumb/4/40/Make-Everyone-Love-You-%28Teen-Girls%29-Step-1.jpg/aid2097423-v4-728px-Make-Everyone-Love-You-%28Teen-Girls%29-Step-1.jpg"));
    }

    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        String userChoice = req.getParameter("choise");

        choice = "Yes".equals(userChoice);

        doGet(req, resp);

    }
}
